def fragment = context.testCase.getTestStepByName("WSMAN Get Api").getProperty("response").value
//def xml = new XmlSlurper().parseText(fragment) 
def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )
def holder = groovyUtils.getXmlHolder( fragment)

def policy = holder.getNodeValue("//l7:Resource/text()")

def holder2 = groovyUtils.getXmlHolder( policy )

def serviceName = holder.getNodeValue("//l7:Name")

def apiId = holder2.getNodeValue("//L7p:ApiPortalIntegration/L7p:ApiId/@stringValue")
def serviceUrl = holder2.getNodeValue("//L7p:SetVariable/L7p:VariableToSet[@stringValue='serviceUrl']/../L7p:Base64Expression/@stringValue")

def encassGuid = holder2.getNodeValue("//L7p:Encapsulated/L7p:EncapsulatedAssertionConfigGuid/@stringValue")
def apiLocation = holder2.getNodeValue("//L7p:Encapsulated//L7p:key[@stringValue='apiLocation']/../L7p:value/@stringValue")
def debugMode = holder2.getNodeValue("//L7p:Encapsulated//L7p:key[@stringValue='debugMode']/../L7p:value/@stringValue")
def sslEnabled = holder2.getNodeValue("//L7p:Encapsulated//L7p:key[@stringValue='sslEnabled']/../L7p:value/@stringValue")

assert serviceName == context.expand( '${#TestCase#serviceName}' )

assert apiId == context.expand( '${#TestCase#ssgApiId}' )
assert serviceUrl == context.expand( '${#TestCase#urlPattern}' ).bytes.encodeBase64().toString()

assert encassGuid == context.expand( '${#Project#standardPolicyTemplateEncassGuid}' )
log.info apiLocation + ":" + context.expand( '${#Project#encassRoutingDestination}')
assert apiLocation == 'http://google.com'
assert debugMode == 'false'
assert sslEnabled == 'true'

def fragment = context.testCase.getTestStepByName("WSMAN Get Updated Api").getProperty("response").value
//def xml = new XmlSlurper().parseText(fragment) 
def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )
def holder = groovyUtils.getXmlHolder( fragment)

def policy = holder.getNodeValue("//l7:Resource/text()")
def serviceName = holder.getNodeValue("//l7:Name")

def holder2 = groovyUtils.getXmlHolder( policy )

def apiId = holder2.getNodeValue("//L7p:ApiPortalIntegration/L7p:ApiId/@stringValue")
def serviceUrl = holder2.getNodeValue("//L7p:SetVariable/L7p:VariableToSet[@stringValue='serviceUrl']/../L7p:Base64Expression/@stringValue")

def encassGuid = holder2.getNodeValue("//L7p:Encapsulated/L7p:EncapsulatedAssertionConfigGuid/@stringValue")
def apiLocation = holder2.getNodeValue("//L7p:Encapsulated//L7p:key[@stringValue='apiLocation']/../L7p:value/@stringValue")
def debugMode = holder2.getNodeValue("//L7p:Encapsulated//L7p:key[@stringValue='debugMode']/../L7p:value/@stringValue")
def sslEnabled = holder2.getNodeValue("//L7p:Encapsulated//L7p:key[@stringValue='sslEnabled']/../L7p:value/@stringValue")

assert serviceName == ( context.expand( '${#TestCase#serviceName}' ) + '-changed' )

assert apiId == context.expand( '${#TestCase#ssgApiId}' ) 
assert serviceUrl == context.expand( '${#TestCase#urlPattern}' ).bytes.encodeBase64().toString() 

assert encassGuid == context.expand( '${#Project#standardPolicyTemplateEncassGuid}' )
log.info apiLocation + ":" + context.expand( '${#Project#encassRoutingDestination}')
assert apiLocation == context.expand( '${#Project#encassRoutingDestination}')
assert debugMode == 'true'
assert sslEnabled == 'false'

