declare namespace wxf='http://schemas.xmlsoap.org/ws/2004/09/transfer';
declare namespace wsmeta='http://schemas.dmtf.org/wbem/wsman/1/wsman/version1.0.0.a/default-addressing-model.xsd';
declare namespace xs='http://www.w3.org/2001/XMLSchema';
declare namespace l7='http://ns.l7tech.com/2010/04/gateway-management';
declare namespace env='http://www.w3.org/2003/05/soap-envelope';
declare namespace wsen='http://schemas.xmlsoap.org/ws/2004/09/enumeration';
//l7:Service/@version

con:config.script:
def fragment = context.testCase.getTestStepByName("WSMAN Get (2)").getProperty("response").value
//def xml = new XmlSlurper().parseText(fragment) 
def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )
def holder = groovyUtils.getXmlHolder( fragment)

def policy = holder.getNodeValue("//l7:Resource/text()")

def holder2 = groovyUtils.getXmlHolder( policy )

//SslAssertion
def sslAssertion = holder2.getNodeValue("//L7p:SslAssertion//L7p:Enabled//@booleanValue='True'")

//SqlAttackProtection
def MsSql = holder2.getNodeValue("//L7p:SqlAttackProtection//L7p:Protections//L7p:item//@stringValue='MsSql'")
def OraSql = holder2.getNodeValue("//L7p:SqlAttackProtection//L7p:Protections//L7p:item//@stringValue='OraSql'")
def SqlMetaText = holder2.getNodeValue("//L7p:SqlAttackProtection//L7p:Protections//L7p:item//@stringValue='SqlMetaText'")
def SqlMeta = holder2.getNodeValue("//L7p:SqlAttackProtection//L7p:Protections//L7p:item//@stringValue='SqlMeta'")


//CodeInjectionProtectionAssertion
def htmlJavaScriptInjection = holder2.getNodeValue("//L7p:CodeInjectionProtectionAssertion//L7p:Protections//L7p:item//@protectionType='htmlJavaScriptInjection'")
def phpEvalInjection = holder2.getNodeValue("//L7p:CodeInjectionProtectionAssertion//L7p:Protections//L7p:item//@protectionType='phpEvalInjection'")
def shellInjection = holder2.getNodeValue("//L7p:CodeInjectionProtectionAssertion//L7p:Protections//L7p:item//@protectionType='shellInjection'")
def ldapDnInjection = holder2.getNodeValue("//L7p:CodeInjectionProtectionAssertion//L7p:Protections//L7p:item//@protectionType='ldapDnInjection'")
def ldapSearchInjection = holder2.getNodeValue("//L7p:CodeInjectionProtectionAssertion//L7p:Protections//L7p:item//@protectionType='ldapSearchInjection'")
def xpathInjection = holder2.getNodeValue("//L7p:CodeInjectionProtectionAssertion//L7p:Protections//L7p:item//@protectionType='xpathInjection'")

def CodeInjectionProtectionAssertion = holder2.getNodeValue("//L7p:CodeInjectionProtectionAssertion//L7p:Enabled")
def IncludeRequestBody = holder2.getNodeValue("//L7p:CodeInjectionProtectionAssertion//L7p:IncludeRequestBody//@booleanValue")
def IncludeRequestUrl = holder2.getNodeValue("//L7p:CodeInjectionProtectionAssertion//L7p:IncludeRequestUrl//@booleanValue")

assert sslAssertion

assert MsSql
assert OraSql
assert SqlMetaText
assert SqlMeta
assert htmlJavaScriptInjection
assert phpEvalInjection
assert shellInjection
assert ldapDnInjection
assert ldapSearchInjection

assert CodeInjectionProtectionAssertion == null
assert IncludeRequestBody
assert IncludeRequestUrl

