    @Test
    public void testSplitFields() throws Exception {
        assertEquals(Arrays.asList(""), AuditRecordCompatibilityVerifier52.splitFields(""));
        assertEquals(Arrays.asList(""), AuditRecordCompatibilityVerifier52.splitFields("\\"));
        assertEquals(Arrays.asList("\\"), AuditRecordCompatibilityVerifier52.splitFields("\\\\"));
        assertEquals(Arrays.asList(":"), AuditRecordCompatibilityVerifier52.splitFields("\\:"));
        assertEquals(Arrays.asList("\\:"), AuditRecordCompatibilityVerifier52.splitFields("\\\\\\:"));
        assertEquals(Arrays.asList("\\:"), AuditRecordCompatibilityVerifier52.splitFields("\\\\\\:\\"));
        assertEquals(Arrays.asList("a"), AuditRecordCompatibilityVerifier52.splitFields("a"));
        assertEquals(Arrays.asList("a", "b"), AuditRecordCompatibilityVerifier52.splitFields("a:b"));
        assertEquals(Arrays.asList("a", "b", "c"), AuditRecordCompatibilityVerifier52.splitFields("a:b:c"));
        assertEquals(Arrays.asList("a", "b", "c", ""), AuditRecordCompatibilityVerifier52.splitFields("a:b:c:"));
        assertEquals(Arrays.asList("a", "", "c", ""), AuditRecordCompatibilityVerifier52.splitFields("a::c:"));
        assertEquals(Arrays.asList("a", ":c", ""), AuditRecordCompatibilityVerifier52.splitFields("a:\\:c:"));
        assertEquals(Arrays.asList("a", "b", "c:"), AuditRecordCompatibilityVerifier52.splitFields("a:b:c\\:"));
        assertEquals(Arrays.asList("a", "b", "[c:d:e]"), AuditRecordCompatibilityVerifier52.splitFields("a:b:[c\\:d\\:e]"));
    }

    @Test
    public void testReplayUpdatesWithDetail() throws Exception {
        replaySignedUpdates(RECORDED_UPDATES_WITHDETAIL);
    }

    @Test
    public void testReplayUpdatesNoDetail() throws Exception {
        replaySignedUpdates(RECORDED_UPDATES_NODETAIL);
    }

    @Test
    public void testReplayUpdatesMessageWithResponse() throws Exception {
        replaySignedUpdates(RECORDED_UPDATES_MESSAGE_WITH_RESPONSE);
    }

    static void replaySignedUpdates(String str) throws Exception {
        @SuppressWarnings({"unchecked"}) List<Pair<Boolean, byte[]>> updates =
                (List<Pair<Boolean, byte[]>>)new ObjectInputStream(new ByteArrayInputStream(HexUtils.decodeBase64(str))).readObject();

        int fieldNum = 0;
        for (Pair<Boolean, byte[]> update : updates) {
            if (update.left) {
                if (PRINTOUT) System.out.println("update byte array : " + HexUtils.hexDump(update.right) + " (" + new String(update.right) + ")");
                byte[] b = update.right;
                if (b.length == 1 && b[0] == ':') {
                    fieldNum++;
                } else if (b.length == 1 && b[0] == '[') {
                    if (PRINTOUT) System.out.println("Open square bracket in field " + fieldNum);
                }
            } else {
                if (PRINTOUT) System.out.println("update single byte: " + HexUtils.hexDump(update.right) + " (" + new String(update.right) + ")");
            }
        }

    }

