    static void testVerifySignedRecord(String rawRec, String signature, String certPem) throws Exception {
        X509Certificate cert = CertUtils.decodeFromPEM(certPem, false);

        boolean result = new AuditRecordCompatibilityVerifier52(cert).verifyAuditRecordSignature(signature, rawRec);
        assertTrue("Signature made in version 5.3 must verify successfully with compatibility verifier", result);

        // Try altering the signed data
        String hackedRawRec = rawRec.replaceAll("Server", "Sevrer");
        hackedRawRec = hackedRawRec.replaceAll("INFO", "IFNO");
        hackedRawRec = hackedRawRec.replaceAll("Start", "Strat");
        hackedRawRec = hackedRawRec.replaceAll("service", "srevice");
        hackedRawRec = hackedRawRec.replaceAll("processed", "procsesed");

        result = new AuditRecordCompatibilityVerifier52(cert).verifyAuditRecordSignature(signature, hackedRawRec);
        assertFalse("Signature sould not verify when signed record is altered", result);

        if (signature != null) {
            // Try altering the signature
            String hackedSig = signature.replace("w", "x");
            result = new AuditRecordCompatibilityVerifier52(cert).verifyAuditRecordSignature(hackedSig, rawRec);
            assertFalse("Signature sould not verify when signature is altered", result);
        }
    }

